import React, { Component } from 'react';
import UserInfo from "./UserInfo";

export class DisplayUser extends Component {
  
  constructor(){
    super();
    this.state = {
      paramValue : ""
    }
  }

  getParamFromUrl(data){
    if(!!data){
      this.setState({
        paramValue : data
      })
    }
  }

  render() {
    return (
      <div className='container mt-5'>
          <h1>Display User Info</h1>
          <hr />
          <p><span className='fw-bold'>ID :</span> {this.props.id}</p>
          <p><span className='fw-bold'>Login :</span> {this.props.login}</p>
          <p><span className='fw-bold'>Bio :</span> {this.props.bio}</p>
          <p><span className='fw-bold'>Location :</span> {this.props.location}</p>
          <p><span className='fw-bold'>Email :</span> {this.props.email}</p>
          <p><span className='fw-bold'>Twitter :</span> {this.props.twitter_username}</p>
          <p><span className='fw-bold'>Followers :</span> {this.props.followers}</p>
          <p><span className='fw-bold'>Following :</span> {this.props.following}</p>
      </div>
    )
  }
}

export default DisplayUser
