import React, { Component } from 'react';
import UserInfo from "./UserInfo";

export class DisplayUser extends Component {
  
  constructor(){
    super();
    this.state = {
      userInfo : {}
    }
    this.getParamFromUrl = this.getParamFromUrl.bind(this);
  }

  getParamFromUrl(param){
    fetch(`https://api.github.com/users/${param}`)
        .then(response => {
            return response.json();
        })
        .then(data => {
            this.setState({userInfo : data})
        })
      
  }

  render() {
    return (
      <div className='container mt-5'>
          <UserInfo getParam={this.getParamFromUrl} />
          <h1>Display User Info</h1>
          <hr />
          <p><span className='fw-bold'>ID :</span> {this.state.userInfo.id == null ? "empty" : this.state.userInfo.id}</p>
          <p><span className='fw-bold'>Login :</span> {this.state.userInfo.login == null ? "empty" : this.state.userInfo.login}</p>
          <p><span className='fw-bold'>Bio :</span> {this.state.userInfo.bio}</p>
          <p><span className='fw-bold'>Location :</span> {this.state.userInfo.location}</p>
          <p><span className='fw-bold'>Email :</span> {this.state.userInfo.email}</p>
          <p><span className='fw-bold'>Twitter :</span> {this.state.userInfo.twitter_username}</p>
          <p><span className='fw-bold'>Followers :</span> {this.state.userInfo.followers}</p>
          <p><span className='fw-bold'>Following :</span> {this.state.userInfo.following}</p>
      </div>
    )
  }
}

export default DisplayUser
